    private function handleFile(string $files)
    {
        $files = explode('.webp', $files);
        $result = [];
        foreach ($files as $file) {
            $path = explode('/', $file);
            $file = array_pop($path);
            if ($file) {
                $result[] = $file . '.webp';
            }
        }

        return $result;
    }

    private function groupImage(array $images, string $prefix = 'app_models_productimage/')
    {
        $result = [];
        foreach ($images as $image) {
            $part = explode('_', $image);
            $name = $part[0];
            $part = explode('.', array_pop($part));
            array_pop($part);
            $part = explode('-', $part[0]);
            array_pop($part);

            $result[$part[0]][] = [
                'url' => $prefix . $image,
                'name' => $name,
            ];
        }

        return $result;
    }
